from itertools import count


def countingValleys(steps, path):
    steps_down = 0
    steps_up = 0
    num_valleys = 0
    for step in range(steps):
        if path[step] == 'D':
            steps_down += 1
        else:
            steps_up += 1
            if steps_down - steps_up == 0:
                num_valleys += 1
    return num_valleys
   
if __name__=="__main__":
    path = ""
    print(len(path))
    print(countingValleys(len(path),path))
    print("-------------------------")
    steps = 8
    path = "DDUUUUDD"
    print(countingValleys(steps,path))